import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import MenuBar from '../components/MenuBar'; // Import the MenuBar
import './LoginForm.css'; // Add CSS for the form styling

const LoginForm = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post('http://localhost:5001/api/users/login', {
        username,
        password,
      });
      const token = res.data.token;
      localStorage.setItem('token', token); // Store JWT in localStorage
      alert('Login successful!');
      if (onLogin) onLogin(); // Notify parent component
      navigate('/'); // Redirect to homepage
    } catch (error) {
      console.error('Error logging in:', error);
      alert('Login failed. Please try again.');
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <form onSubmit={handleLogin} className="login-form">
          <h2>Welcome Back</h2>
          <p className="login-description">Please log in to continue</p>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="login-input"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="login-input"
            required
          />
          <button type="submit" className="login-button">Login</button>
        </form>
      </div>
      <MenuBar /> {/* Add MenuBar at the bottom */}
    </div>
  );
};

export default LoginForm;
