import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import MenuBar from '../components/MenuBar';
import '../css/UploadForm.css'; // Import CSS

const UploadForm = () => {
  const [title, setTitle] = useState('');
  const [file, setFile] = useState(null);
  const [alert, setAlert] = useState(null); // State for managing alerts
  const navigate = useNavigate();

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!file) {
      setAlert({
        title: 'Error',
        message: 'Please select a file to upload!',
        onClose: () => setAlert(null), // Clear alert on close
      });
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('video', file);

    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5001/api/videos/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });

      // Clear form and show success alert
      setTitle('');
      setFile(null);
      setAlert({
        title: 'Success',
        message: 'Video uploaded successfully!',
        onClose: () => {
          setAlert(null); // Clear the alert
          navigate('/profile'); // Redirect to the profile page
        },
      });
    } catch (error) {
      console.error('Error uploading video:', error);
      setAlert({
        title: 'Error',
        message: 'Failed to upload video. Please try again.',
        onClose: () => setAlert(null),
      });
    }
  };

  return (
    <div className="upload-page">
      <div className="upload-container">
        <form onSubmit={handleUpload} className="upload-form">
          <h2>Upload Your Video</h2>
          <input
            type="text"
            placeholder="Enter Video Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="upload-input"
            required
          />
          <label className="file-upload">
            <input
              type="file"
              accept="video/*"
              onChange={(e) => setFile(e.target.files[0])}
              required
            />
            {file ? file.name : 'Choose a video file'}
          </label>
          <button type="submit" className="upload-button">Upload</button>
        </form>
      </div>
      <MenuBar />
      {alert && (
        <div className="custom-alert-overlay">
          <div className="custom-alert">
            <h3>{alert.title}</h3>
            <p>{alert.message}</p>
            <button onClick={alert.onClose} className="alert-button">
              OKAY
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default UploadForm;
