import React, { useState } from 'react';
import axios from 'axios';
import MenuBar from '../components/MenuBar'; // Import MenuBar
import '../css/UploadForm.css'; // Import custom CSS

const UploadForm = () => {
  const [title, setTitle] = useState('');
  const [file, setFile] = useState(null);

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!file) {
      alert('Please select a file to upload!');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('video', file);

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post('http://localhost:5001/api/videos/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });
      alert('Video uploaded successfully!');
      console.log('Upload response:', res.data);
    } catch (error) {
      console.error('Error uploading video:', error);
      alert('Failed to upload video. Please try again.');
    }
  };

  return (
    <div className="upload-page">
      <div className="upload-container">
        <form onSubmit={handleUpload} className="upload-form">
          <h2>Upload Your Video</h2>
          <p className="upload-description">Share your amazing videos with the world!</p>
          <input
            type="text"
            placeholder="Enter Video Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="upload-input"
            required
          />
          <label className="file-upload">
            <input
              type="file"
              accept="video/*"
              onChange={(e) => setFile(e.target.files[0])}
              required
            />
            {file ? file.name : 'Choose a video file'}
          </label>
          <button type="submit" className="upload-button">Upload</button>
        </form>
      </div>
      <MenuBar /> {/* Include the menu bar */}
    </div>
  );
};

export default UploadForm;
