import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CustomAlert from '../components/CustomAlert';
import MenuBar from '../components/MenuBar';
import '../css/UploadForm.css'; // Add any custom CSS for the upload form

const UploadForm = () => {
  const [title, setTitle] = useState('');
  const [file, setFile] = useState(null);
  const [showAlert, setShowAlert] = useState(false); // State to manage alert visibility
  const [isAuthenticated, setIsAuthenticated] = useState(true); // Check user authentication

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsAuthenticated(false); // User is not authenticated
      setShowAlert(true); // Show alert
    }
  }, []);

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!file) {
      alert('Please select a file to upload!');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('video', file);

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post('http://localhost:5001/api/videos/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });
      alert('Video uploaded successfully!');
      console.log('Upload response:', res.data);
    } catch (error) {
      console.error('Error uploading video:', error);
      alert('Failed to upload video. Please try again.');
    }
  };

  if (!isAuthenticated) {
    return (
      <>
        {showAlert && (
          <CustomAlert
            title="Not Signed In"
            message="You need to be signed in to upload videos. Redirecting to the profile page..."
            onClose={() => (window.location.href = '/profile')} // Redirect to profile page
          />
        )}
        <MenuBar /> {/* Show the menu bar */}
      </>
    );
  }

  return (
    <div className="upload-page">
      <form onSubmit={handleUpload} className="upload-form">
        <h2>Upload a Video</h2>
        <input
          type="text"
          placeholder="Video Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <input
          type="file"
          accept="video/*"
          onChange={(e) => setFile(e.target.files[0])}
          required
        />
        <button type="submit">Upload</button>
      </form>
      <MenuBar />
    </div>
  );
};

export default UploadForm;
