import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import VideoCard from './VideoCard';

const VideoFeed = () => {

  const [videos, setVideos] = useState([]);
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);
  const videoRefs = useRef([]);
  const isTransitioning = useRef(false);
  const transitionTimeout = useRef(null);

  const fetchVideos = async () => {
    try {
      const res = await axios.get('http://localhost:5001/api/videos?page=1&limit=10');
      setVideos(res.data);
    } catch (error) {
      console.error('Error fetching videos:', error);
    }
  };

  useEffect(() => {
    fetchVideos();
  }, []);

  const scrollToVideo = (index) => {
    if (videoRefs.current[index]) {
      isTransitioning.current = true;

      videoRefs.current[index].scrollIntoView({
        behavior: 'smooth',
        block: 'start',
      });

      // Reset transition lock
      if (transitionTimeout.current) {
        clearTimeout(transitionTimeout.current);
      }
      transitionTimeout.current = setTimeout(() => {
        isTransitioning.current = false;
      }, 500); // Match the scroll animation duration
    }
  };

  useEffect(() => {
    scrollToVideo(currentVideoIndex);
  }, [currentVideoIndex]);

  const throttle = (func, limit) => {
    let inThrottle;
    return function (...args) {
      if (!inThrottle) {
        func(...args);
        inThrottle = true;
        setTimeout(() => (inThrottle = false), limit);
      }
    };
  };

  const handleWheel = (e) => {
    if (isTransitioning.current) return;

    e.preventDefault();

    const direction = e.deltaY > 0 ? 1 : -1;
    const nextIndex = Math.max(0, Math.min(currentVideoIndex + direction, videos.length - 1));

    if (nextIndex !== currentVideoIndex) {
      setCurrentVideoIndex(nextIndex);
    }
  };

  const throttledWheelHandler = throttle(handleWheel, 700); // Throttle to 700ms (matches animation duration)

  useEffect(() => {
    const videoFeedElement = document.querySelector('.video-feed');

    const handleWheelEvent = (e) => throttledWheelHandler(e);

    videoFeedElement.addEventListener('wheel', handleWheelEvent, { passive: false });

    return () => {
      videoFeedElement.removeEventListener('wheel', handleWheelEvent);
      if (transitionTimeout.current) {
        clearTimeout(transitionTimeout.current);
      }
    };
  }, [videos, currentVideoIndex]);

  return (
    <div className="video-feed" style={{ height: '100vh', overflow: 'hidden' }} >
      {videos.length === 0 && <h3>Loading...</h3>}
      {videos.map((video, index) => (
        <div
          key={video.id}
          ref={(el) => (videoRefs.current[index] = el)}
          className="video-container"
          style={{ height: '100vh' }}
        >
          <VideoCard video={video} isCurrent={index === currentVideoIndex} />
        </div>
      ))}
    </div>
  );
};

export default VideoFeed;
