import React from 'react';
import '../css/CustomAlert.css'; // Import styles

const CustomAlert = ({ title, message, onClose, showSpinner }) => {
  if (!title && !message) return null; // Render nothing if no alert details

  return (
    <div className="custom-alert-overlay">
      <div className="custom-alert">
        <h3>{title}</h3>
        <p>{message}</p>
        {showSpinner && <div className="spinner"></div>} {/* Optional spinner */}
        {onClose && (
          <button onClick={onClose} className="alert-button">
            OKAY
          </button>
        )}
      </div>
    </div>
  );
};

export default CustomAlert;
