import React, { useRef, useEffect } from 'react';

const VideoCard = ({ video, isCurrent }) => {
  const videoRef = useRef();

  useEffect(() => {
    console.log('Rendering video:', video); // Debug log
    console.log('Is current:', isCurrent); // Debug log
    if (isCurrent) {
      videoRef.current?.play();
    } else {
      videoRef.current?.pause();
    }
  }, [isCurrent]);

  return (
    <div className="video-card">
      {video ? (
        <video
          ref={videoRef}
          src="http://localhost:5001${video.url}"
          muted
          loop
          preload="metadata"
          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
        ></video>
      ) : (
        <p>Loading video...</p>
      )}
      <div className="video-info">
        <h3>{video.title}</h3>
        <p>{video.like_count} Likes</p>
        <p>{video.comment_count} Comments</p>
      </div>
    </div>
  );
};

export default VideoCard;
