import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VideoCard from './VideoCard';

const VideoFeed = () => {
  const [videos, setVideos] = useState([]);
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);

  const fetchVideos = async () => {
    try {
      const res = await axios.get('http://localhost:5001/api/videos?page=1&limit=10');
      console.log('Raw Fetched Videos:', res.data); // Debug log

      const updatedVideos = res.data.map((video) => {
        // Ensure the URL is not duplicated
        const isAbsoluteUrl = video.url.startsWith('http://') || video.url.startsWith('https://');
        return {
          ...video,
          url: isAbsoluteUrl ? video.url : `http://localhost:5001${video.url}`,
        };
      });

      console.log('Processed Videos:', updatedVideos); // Debug log
      setVideos(updatedVideos);
    } catch (error) {
      console.error('Error fetching videos:', error);
    }
  };
  useEffect(() => {
    fetchVideos();
  }, []);

  const handleScroll = (e) => {
    const direction = e.deltaY > 0 ? 1 : -1; // Scroll down or up
    setCurrentVideoIndex((prevIndex) => {
      const newIndex = prevIndex + direction;
      console.log('New video index:', newIndex); // Debug log
      return Math.max(0, Math.min(newIndex, videos.length - 1)); // Clamp index
    });
  };

  return (
    <div
      className="video-feed"
      onWheel={handleScroll} // Listen for scroll events
      style={{ height: '100vh', overflow: 'hidden' }}
    >
      {videos.length === 0 && <h3>Loading...</h3>} {/* Display loading message */}
      {videos.map((video, index) => (
        <div
          key={video.id}
          className="video-container"
          style={{
            display: index === currentVideoIndex ? 'block' : 'none', // Show only the current video
            height: '100vh',
          }}
        >
          <VideoCard video={video} isCurrent={index === currentVideoIndex} />
        </div>
      ))}
    </div>
  );
};

export default VideoFeed;
