import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import VideoCard from './VideoCard';

const VideoFeed = () => {
  const [videos, setVideos] = useState([]);
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);
  const videoRefs = useRef([]); // Array of refs for video containers
  const isTransitioning = useRef(false); // Lock scrolling during transition

  const fetchVideos = async () => {
    try {
      const res = await axios.get('http://localhost:5001/api/videos?page=1&limit=10');
      console.log('Fetched videos:', res.data);

      const updatedVideos = res.data.map((video) => ({
        ...video,
        url: `${video.url}`,
      }));

      setVideos(updatedVideos);
    } catch (error) {
      console.error('Error fetching videos:', error);
    }
  };

  useEffect(() => {
    fetchVideos();
  }, []);

  useEffect(() => {
    // Scroll the current video into view
    if (videoRefs.current[currentVideoIndex]) {
      isTransitioning.current = true; // Lock scrolling
      videoRefs.current[currentVideoIndex].scrollIntoView({
        behavior: 'smooth',
        block: 'start',
      });

      // Unlock after the scroll animation completes
      setTimeout(() => {
        isTransitioning.current = false; // Unlock scrolling
      }, 700); // Match this duration to `scrollIntoView`
    }
  }, [currentVideoIndex]);

  const handleWheel = (e) => {
    if (isTransitioning.current) return; // Ignore all scrolls if transition is active

    const direction = e.deltaY > 0 ? 1 : -1; // Determine scroll direction
    const nextIndex = Math.max(0, Math.min(currentVideoIndex + direction, videos.length - 1));

    if (nextIndex !== currentVideoIndex) {
      setCurrentVideoIndex(nextIndex); // Trigger the scroll animation
    }
  };

  // Add/remove event listener for wheel manually to ensure no interference
  useEffect(() => {
    const videoFeedElement = document.querySelector('.video-feed');
    videoFeedElement.addEventListener('wheel', handleWheel);

    return () => {
      videoFeedElement.removeEventListener('wheel', handleWheel);
    };
  }, [currentVideoIndex, videos]);

  return (
    <div
      className="video-feed"
      style={{ height: '100vh', overflow: 'hidden' }}
    >
      {videos.length === 0 && <h3>Loading...</h3>}
      {videos.map((video, index) => (
        <div
          key={video.id}
          ref={(el) => (videoRefs.current[index] = el)} // Set ref for each video container
          className="video-container"
          style={{
            height: '100vh',
          }}
        >
          <VideoCard video={video} isCurrent={index === currentVideoIndex} />
        </div>
      ))}
    </div>
  );
};

export default VideoFeed;
