import React from 'react';
import '../css/CustomAlert.css';

const CustomAlert = ({ title, message, onClose, onConfirm, confirmText, cancelText }) => {
    if (!title && !message) return null;

    const handleOverlayClick = (e) => {
        if (e.target === e.currentTarget && onClose) {
            onClose(); // Close the alert when clicking the overlay
        }
    };

    return (
        <div className="custom-alert-overlay" onClick={handleOverlayClick}>
            <div className="custom-alert">
                <h3>{title}</h3>
                <p>{message}</p>
                <div className="alert-buttons">
                    {onConfirm && (
                        <button onClick={onConfirm} className="alert-button confirm-button">
                            {confirmText || 'Yes'}
                        </button>
                    )}
                    {onClose && (
                        <button onClick={onClose} className="alert-button cancel-button">
                            {cancelText || 'No'}
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

export default CustomAlert;
